import classes from './NegativeBox.module.css';
import React, {useState,useEffect,useHistory} from 'react';
//MUI
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import IconButton from '@mui/material/IconButton';
//comps
import Addbutton from './Addbutton'


function NegativeBox(props){

    //punkte: {punkt1,punkt2,punkt3,punkt4,punkt5}
    //punkt1: {kommentar, maßnahme}
    const [punkte,setPunkte] = useState({})
    const [textFieldAmount,setTextFieldAmount] = useState(0)



    return (
        <div className={classes.box}>
            <div className={classes.boxHeading}>
                {props.negativText}
            </div>

            { textFieldAmount>=1 && 
                <div>
                    <TextField value={punkte.punkt1.kommentar} onChange={e=>{setKommentare(prev=>({...prev, kommentar1: e.target.value   }))}}  label="1. Kommentar" variant="standard" sx={{width:'100%'}}/>   
                    <TextField value={punkte.punkt1.maßnahme} onChange={e=>{setKommentare(prev=>({...prev, kommentar1: e.target.value   }))}}  label="1. Kommentar" variant="standard" sx={{width:'100%'}}/>   
                </div>
            }



            <div style={{marginTop:'25px'}}>
            { textFieldAmount<=4 && <Addbutton onClick={()=>{setTextFieldAmount(textFieldAmount+1)}} text={"Kommentar hinzufügen"}/>  }
            </div>
        </div>
    );
}

export default NegativeBox;
