import classes from './Positiv.module.css';
import React, {useState,useEffect,useHistory} from 'react';
//MUI
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import IconButton from '@mui/material/IconButton';
//comps
import Addbutton from './Addbutton'


function PositivBox(props){

    const [kommentare,setKommentare] = useState({})
    const [textFieldAmount,setTextFieldAmount] = useState(1)


    function addTextField(){
        setTextFieldAmount(textFieldAmount+1)
    }

    return (
        <div className={classes.box}>
            <div className={classes.boxHeading}>
                1  Zusammenarbeit im Projekt/ Teamspirit und Konfliktkultur
            </div>

            <TextField value={kommentare.kommentar1}  label="Kommentar" variant="standard" sx={{width:'100%'}}/>
            { textFieldAmount<=4 && <TextField value={kommentare.kommentar2}  label="Kommentar" variant="standard" sx={{width:'100%'}}/>   }
            { textFieldAmount<=4 && <TextField value={kommentare.kommentar3}  label="Kommentar" variant="standard" sx={{width:'100%'}}/>   }
            { textFieldAmount<=4 && <TextField value={kommentare.kommentar4}  label="Kommentar" variant="standard" sx={{width:'100%'}}/>   }
            { textFieldAmount<=4 && <TextField value={kommentare.kommentar5}  label="Kommentar" variant="standard" sx={{width:'100%'}}/>   }

            { textFieldAmount<=4 && <Addbutton onClick={addTextField} text={"Portfolio hinzufügen"}/>  }

        </div>
    );
}

export default PositivBox;
