import classes from './PositivBox.module.css';
import React, {useState,useEffect,useHistory} from 'react';
//MUI
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import IconButton from '@mui/material/IconButton';
//comps
import Addbutton from './Addbutton'


function PositivBox(props){

    const [kommentare,setKommentare] = useState({})
    const [textFieldAmount,setTextFieldAmount] = useState(1)


    function addTextField(){
        setTextFieldAmount(textFieldAmount+1)
    }

    return (
        <div className={classes.box}>
            <div className={classes.boxHeading}>
                {props.positivText}
            </div>

            <TextField value={kommentare.kommentar1}  label="1. Kommentar" variant="standard" sx={{width:'100%'}}/>
            { textFieldAmount>=2 && <TextField value={kommentare.kommentar2}  label="2. Kommentar" variant="standard" sx={{width:'100%'}}/>   }
            { textFieldAmount>=3 && <TextField value={kommentare.kommentar3}  label="3. Kommentar" variant="standard" sx={{width:'100%'}}/>   }
            { textFieldAmount>=4 && <TextField value={kommentare.kommentar4}  label="4. Kommentar" variant="standard" sx={{width:'100%'}}/>   }
            { textFieldAmount>=5 && <TextField value={kommentare.kommentar5}  label="5. Kommentar" variant="standard" sx={{width:'100%'}}/>   }

            <div style={{marginTop:'25px'}}>
            { textFieldAmount<=4 && <Addbutton onClick={addTextField} text={"Kommentar hinzufügen"}/>  }
            </div>
        </div>
    );
}

export default PositivBox;
